<template>    <div class="header">        <!-- 折叠按钮 -->        <div class="collapse-btn" @click="collapseChage">            <i v-if="!collapse" class="el-icon-d-arrow-left"></i>            <i v-else class="el-icon-d-arrow-right"></i>        </div>        <div class="logo">测试管理平台</div>        <div class="header-right">            <div class="header-user-con">                <!-- 用户头像 -->                <div class="user-avator">                    <!--<img src="../../assets/img/img.jpg"/>-->                    <img :src="gravatar"/>                </div>                <!-- 用户名下拉菜单 -->                <el-dropdown class="user-name">                    <span class="el-dropdown-link">                        {{username}}                        <i class="el-icon-caret-bottom"></i>                    </span>                    <el-dropdown-menu slot="dropdown">                        <el-dropdown-item><a @click="logout">修改密码</a></el-dropdown-item>                        <el-dropdown-item><a @click="logout">退出登录</a></el-dropdown-item>                    </el-dropdown-menu>                </el-dropdown>            </div>        </div>    </div></template><script>    import bus from './bus';    export default {        data() {            return {                collapse: false,                name:'未登录',                gravata:require("../../assets/img/img.jpg")            }        },        computed: {            username() {                let username = localStorage.getItem('username');                return username ? username : this.name;            },            gravatar(){                let gravatar=localStorage.getItem('gravatar');                return gravatar ? gravatar : this.gravata;            }        },        methods: {            // 用户名下拉菜单选择事件            logout: function () {                let that = this;                localStorage.removeItem('username');                that.$router.push('login');            },            // 侧边栏折叠            collapseChage() {                this.collapse = !this.collapse;                bus.$emit('collapse', this.collapse);            },        },    }</script><style scoped>    .header {        position: relative;        box-sizing: border-box;        width: 100%;        height: 70px;        font-size: 22px;        color: #fff;    }    .collapse-btn {        float: left;        padding: 0 21px;        cursor: pointer;        line-height: 70px;    }    .header .logo {        float: left;        width: 250px;        line-height: 70px;    }    .header-right {        float: right;        padding-right: 50px;    }    .header-user-con {        display: flex;        height: 70px;        align-items: center;    }    .btn-bell {        color: #fff;    }    .user-name {        margin-left: 10px;    }    .user-avator {        margin-left: 20px;    }    .user-avator img {        display: block;        width: 40px;        height: 40px;        border-radius: 50%;    }    .el-dropdown-link {        color: #fff;        cursor: pointer;    }</style>