<template>  <el-carousel type="card" :height="bannerHeight + 'px'">    <el-carousel-item v-for="item in img_list" :key="item">      <img :src="item" alt="">    </el-carousel-item>  </el-carousel></template><script>  export default {    name: "dashboard",    data() {      return {        // 图片地址数组        img_list: [          require("../../assets/img/001.jpg"),          require("../../assets/img/002.jpg"),          require("../../assets/img/003.jpg"),          require("../../assets/img/004.jpg"),          require("../../assets/img/005.jpg"),          require("../../assets/img/006.jpg"),        ],        // // 图片父容器高度        bannerHeight: 800,        // // 浏览器宽度        screenWidth: 0,      }    },    methods: {      setSize: function () {        // 通过浏览器宽度(图片宽度)计算高度        this.bannerHeight = 800 / 1920 * this.screenWidth;      },    },    mounted() {      // 首次加载时,需要调用一次      this.screenWidth = window.innerWidth;      this.setSize();      // 窗口大小发生改变时,调用一次      window.onresize = () => {        this.screenWidth = window.innerWidth;        this.setSize();      }    }  }</script><style scoped>  .el-carousel__item h3 {    color: #475669;    font-size: 14px;    opacity: 0.75;    line-height: 200px;    margin: 0;  }  .el-carousel__item:nth-child(2n) {    background-color: #99a9bf;  }  .el-carousel__item:nth-child(2n+1) {    background-color: #d3dce6;  }  img {    /*设置图片宽度和浏览器宽度一致*/    width: 100%;    height: inherit;  }</style>